0a0 \\u00a0 p.add(embed);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 p.add(save); \\u00a0\\u00a0<\\/p>\\r\\n<p>\\u00a0 \\u00a0 p.add(reset);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 this.getContentPane().add(p, BorderLayout.SOUTH);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 open.addActionListener(this);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 embed.addActionListener(this);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 save.addActionListener(this); \\u00a0\\u00a0<\\/p>\\r\\n<p>\\u00a0 \\u00a0 reset.addActionListener(this);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 open.setMnemonic(''O'');<\\/p>\\r\\n<p>\\u00a0 \\u00a0 embed.setMnemonic(''E'');<\\/p>\\r\\n<p>\\u00a0 \\u00a0 save.setMnemonic(''S'');<\\/p>\\r\\n<p>\\u00a0 \\u00a0 reset.setMnemonic(''R'');<\\/p>\\r\\n<p>\\u00a0 \\u00a0\\u00a0<\\/p>\\r\\n<p>\\u00a0 \\u00a0 p = new JPanel(new GridLayout(1,1));<\\/p>\\r\\n<p>\\u00a0 \\u00a0 p.add(new JScrollPane(message));<\\/p>\\r\\n<p>\\u00a0 \\u00a0 message.setFont(new Font(\\"Arial\\",Font.BOLD,20));<\\/p>\\r\\n<p>\\u00a0 \\u00a0 p.setBorder(BorderFactory.createTitledBorder(\\"Message to be embedded\\"));<\\/p>\\r\\n<p>\\u00a0 \\u00a0 this.getContentPane().add(p, BorderLayout.NORTH);<\\/p>\\r\\n<p>\\u00a0 \\u00a0\\u00a0<\\/p>\\r\\n<p>\\u00a0 \\u00a0 sp.setLeftComponent(originalPane);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 sp.setRightComponent(embeddedPane);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 originalPane.setBorder(BorderFactory.createTitledBorder(\\"Original Image\\"));<\\/p>\\r\\n<p>\\u00a0 \\u00a0 embeddedPane.setBorder(BorderFactory.createTitledBorder(\\"Steganographed Image\\"));<\\/p>\\r\\n<p>\\u00a0 \\u00a0 this.getContentPane().add(sp, BorderLayout.CENTER);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 }<\\/p>\\r\\n<p>\\u00a0<\\/p>\\r\\n<p>\\u00a0public void actionPerformed(ActionEvent ae) {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 Object o = ae.getSource();<\\/p>\\r\\n<p>\\u00a0 \\u00a0 if(o == open)<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0openImage();<\\/p>\\r\\n<p>\\u00a0 \\u00a0 else if(o == embed)<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0embedMessage();<\\/p>\\r\\n<p>\\u00a0 \\u00a0 else if(o == save)\\u00a0<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0saveImage();<\\/p>\\r\\n<p>\\u00a0 \\u00a0 else if(o == reset)\\u00a0<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0resetInterface();<\\/p>\\r\\n<p>\\u00a0 \\u00a0 }<\\/p>\\r\\n<p>\\u00a0<\\/p>\\r\\n<p>\\u00a0private java.io.File showFileDialog(final boolean open) {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 JFileChooser fc = new JFileChooser(\\"Open an image\\");<\\/p>\\r\\n<p>\\u00a0 \\u00a0 javax.swing.filechooser.FileFilter ff = new javax.swing.filechooser.FileFilter() {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0public boolean accept(java.io.File f) {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 String name = f.getName().toLowerCase();<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 if(open)<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0return f.isDirectory() || name.endsWith(\\".jpg\\") || name.endsWith(\\".jpeg\\") ||<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 name.endsWith(\\".png\\") || name.endsWith(\\".gif\\") || name.endsWith(\\".tiff\\") ||<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 name.endsWith(\\".bmp\\") || name.endsWith(\\".dib\\");<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 return f.isDirectory() || name.endsWith(\\".png\\") || \\u00a0 \\u00a0name.endsWith(\\".bmp\\");<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 }<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0public String getDescription() {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 if(open)<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0return \\"Image (*.jpg, *.jpeg, *.png, *.gif, *.tiff, *.bmp, *.dib)\\";<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 return \\"Image (*.png, *.bmp)\\";<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 }<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0};<\\/p>\\r\\n<p>\\u00a0 \\u00a0 fc.setAcceptAllFileFilterUsed(false);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 fc.addChoosableFileFilter(ff);<\\/p>\\r\\n<p>\\u00a0<\\/p>\\r\\n<p>\\u00a0 \\u00a0 java.io.File f = null;<\\/p>\\r\\n<p>\\u00a0 \\u00a0 if(open &amp;&amp; fc.showOpenDialog(this) == fc.APPROVE_OPTION)<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0f = fc.getSelectedFile();<\\/p>\\r\\n<p>\\u00a0 \\u00a0 else if(!open &amp;&amp; fc.showSaveDialog(this) == fc.APPROVE_OPTION)<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0f = fc.getSelectedFile();<\\/p>\\r\\n<p>\\u00a0 \\u00a0 return f;<\\/p>\\r\\n<p>\\u00a0 \\u00a0 }<\\/p>\\r\\n<p>\\u00a0<\\/p>\\r\\n<p>\\u00a0private void openImage() {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 java.io.File f = showFileDialog(true);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 try { \\u00a0\\u00a0<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0sourceImage = ImageIO.read(f);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0JLabel l = new JLabel(new ImageIcon(sourceImage));<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0originalPane.getViewport().add(l);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0this.validate();<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0} catch(Exception ex) { ex.printStackTrace(); }<\\/p>\\r\\n<p>\\u00a0 \\u00a0 }<\\/p>\\r\\n<p>\\u00a0<\\/p>\\r\\n<p>\\u00a0private void embedMessage() {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 String mess = message.getText();<\\/p>\\r\\n<p>\\u00a0 \\u00a0 embeddedImage = sourceImage.getSubimage(0,0,<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0sourceImage.getWidth(),sourceImage.getHeight());<\\/p>\\r\\n<p>\\u00a0 \\u00a0 embedMessage(embeddedImage, mess);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 JLabel l = new JLabel(new ImageIcon(embeddedImage));<\\/p>\\r\\n<p>\\u00a0 \\u00a0 embeddedPane.getViewport().add(l);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 this.validate();<\\/p>\\r\\n<p>\\u00a0 \\u00a0 }<\\/p>\\r\\n<p>\\u00a0<\\/p>\\r\\n<p>\\u00a0private void embedMessage(BufferedImage img, String mess) {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 int messageLength = mess.length();<\\/p>\\r\\n<p>\\u00a0 \\u00a0\\u00a0<\\/p>\\r\\n<p>\\u00a0 \\u00a0 int imageWidth = img.getWidth(), imageHeight = img.getHeight(),<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0imageSize = imageWidth * imageHeight;<\\/p>\\r\\n<p>\\u00a0 \\u00a0 if(messageLength * 8 + 32 &gt; imageSize) {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0JOptionPane.showMessageDialog(this, \\"Message is too long for the chosen image\\",<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 \\"Message too long!\\", JOptionPane.ERROR_MESSAGE);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0return;<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0}<\\/p>\\r\\n<p>\\u00a0 \\u00a0 embedInteger(img, messageLength, 0, 0);<\\/p>\\r\\n<p>\\u00a0<\\/p>\\r\\n<p>\\u00a0 \\u00a0 byte b[] = mess.getBytes();<\\/p>\\r\\n<p>\\u00a0 \\u00a0 for(int i=0; i&lt;b.length; i++)<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0embedByte(img, b[i], i*8+32, 0);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 }<\\/p>\\r\\n<p>\\u00a0<\\/p>\\r\\n<p>\\u00a0private void embedInteger(BufferedImage img, int n, int start, int storageBit) {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 int maxX = img.getWidth(), maxY = img.getHeight(),\\u00a0<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0startX = start\\/maxY, startY = start - startX*maxY, count=0;<\\/p>\\r\\n<p>\\u00a0 \\u00a0 for(int i=startX; i&lt;maxX &amp;&amp; count&lt;32; i++) {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0for(int j=startY; j&lt;maxY &amp;&amp; count&lt;32; j++) {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 int rgb = img.getRGB(i, j), bit = getBitValue(n, count);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 rgb = setBitValue(rgb, storageBit, bit);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 img.setRGB(i, j, rgb);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 count++;<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 }<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0}<\\/p>\\r\\n<p>\\u00a0 \\u00a0 }<\\/p>\\r\\n<p>\\u00a0<\\/p>\\r\\n<p>\\u00a0private void embedByte(BufferedImage img, byte b, int start, int storageBit) {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 int maxX = img.getWidth(), maxY = img.getHeight(),\\u00a0<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0startX = start\\/maxY, startY = start - startX*maxY, count=0;<\\/p>\\r\\n<p>\\u00a0 \\u00a0 for(int i=startX; i&lt;maxX &amp;&amp; count&lt;8; i++) {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0for(int j=startY; j&lt;maxY &amp;&amp; count&lt;8; j++) {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 int rgb = img.getRGB(i, j), bit = getBitValue(b, count);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 rgb = setBitValue(rgb, storageBit, bit);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 img.setRGB(i, j, rgb);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 count++;<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 }<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0}<\\/p>\\r\\n<p>\\u00a0 \\u00a0 }<\\/p>\\r\\n<p>\\u00a0<\\/p>\\r\\n<p>\\u00a0private void saveImage() {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 if(embeddedImage == null) {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0JOptionPane.showMessageDialog(this, \\"No message has been embedded!\\",\\u00a0<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 \\"Nothing to save\\", JOptionPane.ERROR_MESSAGE);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0return;<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0}<\\/p>\\r\\n<p>\\u00a0 \\u00a0 java.io.File f = showFileDialog(false);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 String name = f.getName();<\\/p>\\r\\n<p>\\u00a0 \\u00a0 String ext = name.substring(name.lastIndexOf(\\".\\")+1).toLowerCase();<\\/p>\\r\\n<p>\\u00a0 \\u00a0 if(!ext.equals(\\"png\\") &amp;&amp; !ext.equals(\\"bmp\\") &amp;&amp; \\u00a0 !ext.equals(\\"dib\\")) {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 ext = \\"png\\";<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 f = new java.io.File(f.getAbsolutePath()+\\".png\\");<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0 \\u00a0 }<\\/p>\\r\\n<p>\\u00a0 \\u00a0 try {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0if(f.exists()) f.delete();<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0ImageIO.write(embeddedImage, ext.toUpperCase(), f);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0} catch(Exception ex) { ex.printStackTrace(); }<\\/p>\\r\\n<p>\\u00a0 \\u00a0 }<\\/p>\\r\\n<p>\\u00a0<\\/p>\\r\\n<p>\\u00a0private void resetInterface() {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 message.setText(\\"\\");<\\/p>\\r\\n<p>\\u00a0 \\u00a0 originalPane.getViewport().removeAll();<\\/p>\\r\\n<p>\\u00a0 \\u00a0 embeddedPane.getViewport().removeAll();<\\/p>\\r\\n<p>\\u00a0 \\u00a0 sourceImage = null;<\\/p>\\r\\n<p>\\u00a0 \\u00a0 embeddedImage = null;<\\/p>\\r\\n<p>\\u00a0 \\u00a0 sp.setDividerLocation(0.5);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 this.validate();<\\/p>\\r\\n<p>\\u00a0 \\u00a0 }<\\/p>\\r\\n<p>\\u00a0<\\/p>\\r\\n<p>\\u00a0private int getBitValue(int n, int location) {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 int v = n &amp; (int) Math.round(Math.pow(2, location));<\\/p>\\r\\n<p>\\u00a0 \\u00a0 return v==0?0:1;<\\/p>\\r\\n<p>\\u00a0 \\u00a0 }<\\/p>\\r\\n<p>\\u00a0<\\/p>\\r\\n<p>\\u00a0private int setBitValue(int n, int location, int bit) {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 int toggle = (int) Math.pow(2, location), bv = getBitValue(n, location);<\\/p>\\r\\n<p>\\u00a0 \\u00a0 if(bv == bit)<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0return n;<\\/p>\\r\\n<p>\\u00a0 \\u00a0 if(bv == 0 &amp;&amp; bit == 1)<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0n |= toggle;<\\/p>\\r\\n<p>\\u00a0 \\u00a0 else if(bv == 1 &amp;&amp; bit == 0)<\\/p>\\r\\n<p>\\u00a0 \\u00a0 \\u00a0 \\u00a0n ^= toggle;<\\/p>\\r\\n<p>\\u00a0 \\u00a0 return n;<\\/p>\\r\\n<p>\\u00a0 \\u00a0 }<\\/p>\\r\\n<p>\\u00a0<\\/p>\\r\\n<p>\\u00a0public static void main(String arg[]) {<\\/p>\\r\\n<p>\\u00a0 \\u00a0 new EmbedMessage();<\\/p>\\r\\n<p>\\u00a0 \\u00a0 }<\\/p>\\r\\n<p>\\u00a0}<\\/p>\\r\\n<p>\\u00a0<\\/p>","fulltext":"","state":1,"catid":"78","created":"2015-03-31 00:32:53","created_by":"626","created_by_alias":"","modified":"","modified_by":n